import{_ as e,c as o,o as i,ae as r}from"./chunks/framework.DfUtnrnE.js";const m=JSON.parse('{"title":"📝 Exportación a PDF","description":"","frontmatter":{},"headers":[],"relativePath":"funcionalidades/exportar.md","filePath":"funcionalidades/exportar.md"}'),t={name:"funcionalidades/exportar.md"};function l(n,a,s,d,c,p){return i(),o("div",null,a[0]||(a[0]=[r('<h1 id="📝-exportacion-a-pdf" tabindex="-1">📝 Exportación a PDF <a class="header-anchor" href="#📝-exportacion-a-pdf" aria-label="Permalink to &quot;📝 Exportación a PDF&quot;">​</a></h1><p>La funcionalidad de <strong>Exportación a PDF</strong> permite a usuarios y agentes descargar un ticket de soporte completo en formato PDF, incluyendo:</p><ul><li>Detalles del ticket</li><li>Conversaciones con la IA o el agente</li><li>Archivos adjuntos (referenciados)</li><li>Fecha y estado del ticket</li></ul><p>Esto facilita el respaldo externo, impresión o envío por otros medios como correo electrónico.</p><hr><h2 id="🎯-objetivos" tabindex="-1">🎯 Objetivos <a class="header-anchor" href="#🎯-objetivos" aria-label="Permalink to &quot;🎯 Objetivos&quot;">​</a></h2><ul><li>Facilitar auditorías o reportes de soporte</li><li>Proveer documentación para usuarios finales</li><li>Permitir respaldo físico o digital de la atención brindada</li></ul><hr><h2 id="⚙️-¿como-funciona" tabindex="-1">⚙️ ¿Cómo funciona? <a class="header-anchor" href="#⚙️-¿como-funciona" aria-label="Permalink to &quot;⚙️ ¿Cómo funciona?&quot;">​</a></h2><ol><li>El usuario o agente hace clic en el botón &quot;Exportar a PDF&quot; en la vista del ticket.</li><li>Se genera un documento PDF con formato profesional y estructura clara.</li><li>Se descarga automáticamente o se abre en una nueva pestaña para impresión.</li></ol><hr><h2 id="🧰-herramientas-utilizadas" tabindex="-1">🧰 Herramientas utilizadas <a class="header-anchor" href="#🧰-herramientas-utilizadas" aria-label="Permalink to &quot;🧰 Herramientas utilizadas&quot;">​</a></h2><ul><li><p><strong>DOMPDF (Laravel)</strong><br> Utilizado para generar el PDF desde una vista Blade. Permite crear diseños personalizados y estructurados con HTML/CSS.</p></li><li><p><strong>Snappy PDF (opcional)</strong><br> Alternativa basada en <code>wkhtmltopdf</code> para mayor control sobre estilos y compatibilidad con CSS avanzado.</p></li></ul><hr><h2 id="📄-ejemplo-visual-del-contenido-del-pdf" tabindex="-1">📄 Ejemplo visual del contenido del PDF <a class="header-anchor" href="#📄-ejemplo-visual-del-contenido-del-pdf" aria-label="Permalink to &quot;📄 Ejemplo visual del contenido del PDF&quot;">​</a></h2>',15)]))}const h=e(t,[["render",l]]);export{m as __pageData,h as default};
