import{_ as a,c as t,o,ae as s}from"./chunks/framework.DfUtnrnE.js";const m=JSON.parse('{"title":"📋 Descripción del Proyecto","description":"","frontmatter":{},"headers":[],"relativePath":"introduccion/proyecto.md","filePath":"introduccion/proyecto.md"}'),i={name:"introduccion/proyecto.md"};function r(n,e,c,l,d,u){return o(),t("div",null,e[0]||(e[0]=[s('<h1 id="📋-descripcion-del-proyecto" tabindex="-1">📋 Descripción del Proyecto <a class="header-anchor" href="#📋-descripcion-del-proyecto" aria-label="Permalink to &quot;📋 Descripción del Proyecto&quot;">​</a></h1><p><strong>Smart AI Support Desk</strong> es una plataforma integral de soporte técnico diseñada para optimizar la atención al cliente mediante el uso de <strong>inteligencia artificial</strong> y agentes humanos en tiempo real.</p><hr><h2 id="🎯-funcionalidades-principales" tabindex="-1">🎯 Funcionalidades principales <a class="header-anchor" href="#🎯-funcionalidades-principales" aria-label="Permalink to &quot;🎯 Funcionalidades principales&quot;">​</a></h2><p>El sistema permite:</p><ul><li>🎫 <strong>Gestión de tickets</strong>: creación, seguimiento y cierre de solicitudes de soporte.</li><li>🤖 <strong>Respuestas automáticas con IA</strong>: integración con IA GPT para resolver dudas frecuentes al instante.</li><li>👨‍💼 <strong>Intervención de agentes humanos</strong>: atención manual para consultas complejas o sensibles.</li><li>📎 <strong>Archivos adjuntos</strong>: los usuarios pueden adjuntar capturas de pantalla o documentos relevantes al ticket.</li><li>🔔 <strong>Notificaciones inteligentes</strong>: alertas visuales y sonoras cuando hay nuevos tickets o respuestas.</li><li>📤 <strong>Exportación de datos</strong>: generación de reportes en PDF para análisis o archivo físico.</li></ul><hr><h2 id="🔧-caracteristicas-tecnicas" tabindex="-1">🔧 Características técnicas <a class="header-anchor" href="#🔧-caracteristicas-tecnicas" aria-label="Permalink to &quot;🔧 Características técnicas&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Categoría</th><th>Descripción</th></tr></thead><tbody><tr><td>🧠 Inteligencia Artificial</td><td>Uso de IA (GPT) para respuestas contextuales y rápidas.</td></tr><tr><td>🧵 Multicanalidad</td><td>Interfaz web y posibilidad de integrar WhatsApp o email en futuras versiones.</td></tr><tr><td>🔐 Seguridad</td><td>Autenticación de usuarios con Laravel Sanctum.</td></tr><tr><td>🎨 UI Profesional</td><td>Interfaz moderna desarrollada en Vue 3 con TailwindCSS.</td></tr><tr><td>📡 Tiempo real</td><td>Uso de WebSockets para notificaciones instantáneas.</td></tr><tr><td>📈 Escalabilidad</td><td>Pensado para crecer modularmente según necesidades.</td></tr></tbody></table><hr><h2 id="📌-casos-de-uso-tipicos" tabindex="-1">📌 Casos de uso típicos <a class="header-anchor" href="#📌-casos-de-uso-tipicos" aria-label="Permalink to &quot;📌 Casos de uso típicos&quot;">​</a></h2><ol><li><strong>Usuario final</strong> envía una solicitud desde el portal de soporte.</li><li>El sistema analiza el contenido y, si es posible, <strong>responde automáticamente usando IA</strong>.</li><li>Si no hay respuesta automática o el usuario lo solicita, un <strong>agente humano interviene</strong>.</li><li>Todas las interacciones quedan registradas y son <strong>notificadas en tiempo real</strong>.</li><li>El usuario puede <strong>descargar un resumen en PDF</strong> de su caso al finalizar.</li></ol><hr><h2 id="🚀-¿por-que-este-proyecto" tabindex="-1">🚀 ¿Por qué este proyecto? <a class="header-anchor" href="#🚀-¿por-que-este-proyecto" aria-label="Permalink to &quot;🚀 ¿Por qué este proyecto?&quot;">​</a></h2><p>Este proyecto fue diseñado para:</p><ul><li>Poner en práctica conocimientos reales de desarrollo full-stack.</li><li>Aplicar inteligencia artificial a procesos cotidianos.</li><li>Aprender a documentar y presentar proyectos como en entornos empresariales.</li><li>Ser la base de futuras soluciones SaaS con componentes reusables y arquitectura sólida.</li></ul><hr><blockquote><p>&quot;Smart AI Support Desk no solo resuelve tickets, <strong>resuelve procesos completos de comunicación</strong>, haciendo más eficiente la relación entre clientes y soporte técnico.&quot;</p></blockquote>',18)]))}const h=a(i,[["render",r]]);export{m as __pageData,h as default};
